@import url(https://fonts.googleapis.com/css?family=Roboto);

.banner {
  font-family: Roboto, Arial, Helvetica, sans-serif;
  position: relative;
  width: 100%;
  min-width: 500px;
}
.banner h1 {
  font-size: 30px;
}
.banner header {
  position: absolute;
  top: 20px;
  width: 100%;
  padding: 0 20px;
  text-align: left;
  color: red;
}
.banner img {
  width: 100%;
  height: auto;
}
.banner a {
  position: absolute;
  bottom: 20px;
  background-color: rgba(0, 0, 0, 0.5);
  color: red;
  right: 20px;
  padding: 10px;
  text-decoration: none;
}
.banner a:hover {
  background-color: rgba(0, 0, 0, 1);
}

files./src/index.js:
import './styles.css';
import { ContentClient } from 'dc-delivery-sdk-js';

const deliveryKey = 'banner-example';
const client = new ContentClient({ hubName: 'ampengineering' });

client
  .getContentItemByKey(deliveryKey)
  .then(response => renderBanner(response.body));

function renderBanner(banner) {
  if (!banner) {
    return;
  }
  const { headline, strapline, background, link } = banner;
  const template = `
<section class="banner">
  <header>
    <h1>${headline}</h1>
    <h2>${strapline}</h2>
  </header>
  <img src="${background.image.url().build()}" alt="${background.alt}" />
  <a href="${link.url}"> ${link.title} </a>
</section>
`;
  document.body.innerHTML = template;
}
